mapper.insert:
INSERT INTO tb_notice (
    id, title, content, writer, created_date
) VALUES (
             #{id}
         , #{title}
         , #{content}
         , #{writer}
         , NOW()
         )

mapper.update:
UPDATE tb_notice
SET
    created_date = NOW()
  , title = #{title}
  , content = #{content}
  , writer = #{writer}
WHERE
    id = #{id}
